class BaseMediaInterface {
    permissions;
    constructor() {
        this.permissions = {
            audio: 'NOT_REQUESTED',
            video: 'NOT_REQUESTED',
            screenshare: 'NOT_REQUESTED',
        };
    }
    async getAudioInputDevices() {
        const availableDevices = await this.getAvailableDevices();
        return availableDevices.filter((device) => device.kind === 'audioinput');
    }
    async getVideoInputDevices() {
        const availableDevices = await this.getAvailableDevices();
        return availableDevices.filter((device) => device.kind === 'videoinput');
    }
    async getAudioOutputDevices() {
        const availableDevices = await this.getAvailableDevices();
        return availableDevices.filter((device) => device.kind === 'audiooutput');
    }
}
export default BaseMediaInterface;
