import HiveNodeClient from './HiveNodeClient';
import MediasoupRoomNodeClient from './MediasoupRoomNodeClient';
let roomNodeClient;
export async function setupHiveRoomNodeClient(options) {
    if (roomNodeClient)
        throw new Error('Room Node Client already setup');
    roomNodeClient = await HiveNodeClient.init(options);
}
export async function setupMediaSoupRoomNodeClient(options) {
    if (roomNodeClient?.roomJoined)
        throw new Error('Room Node Client already setup');
    if (roomNodeClient)
        roomNodeClient = undefined;
    roomNodeClient = await MediasoupRoomNodeClient.init(options);
}
export async function cleanupRoomNodeClient() {
    roomNodeClient = undefined;
}
export function getRoomNodeClient() {
    return roomNodeClient;
}
