import DyteLogger from '../client/internals/DyteLogger';
export default {
    setItem: (key, value) => {
        try {
            localStorage.setItem(key, value);
        }
        catch (error) {
            DyteLogger.error('LocalStorage::setItem::crashed', { error, localStorage: { key, value } });
        }
    },
    getItem: (key) => {
        try {
            return localStorage.getItem(key);
        }
        catch (error) {
            DyteLogger.error('LocalStorage::getItem::crashed', { error, localStorage: { key } });
        }
        return null;
    },
};
