export default function createSafeToLogError(ex) {
    const formattedError = {};
    if (typeof ex?.code === 'number') {
        formattedError.code = ex.code;
    }
    if (typeof ex?.code === 'string') {
        formattedError.code = ex.code.substring(0, 100);
    }
    if (typeof ex?.name === 'string') {
        formattedError.name = ex.name.substring(0, 500);
    }
    if (typeof ex?.message === 'string') {
        formattedError.message = ex.message.substring(0, 500);
    }
    if (typeof ex?.reason === 'string') {
        formattedError.reason = ex.reason.substring(0, 500);
    }
    if (typeof ex?.stack === 'string') {
        formattedError.stack = ex.stack.substring(0, 500);
    }
    return formattedError;
}
