declare type RTCStatType = 'outbount-rtp' | 'inbound-rtp' | 'candidate-pair';
interface BaseNetworkPayload {
    transport: 'send' | 'receive';
    timestamp: number;
    type: RTCStatType;
    kind?: 'audio' | 'video';
    id: string;
}
interface CandidatePairPayload extends BaseNetworkPayload {
    rtt: number;
    bytesSent: number;
    bytesReceived: number;
}
interface InboundRtpPayload extends BaseNetworkPayload {
    jitter: number;
    bytesReceived: number;
    packetsLost: number;
}
interface OutbountRtpPayload extends BaseNetworkPayload {
    bytesSent: number;
    packetSendDelay: number;
}
declare const formatGetStatsPayload: (data: any) => CandidatePairPayload | InboundRtpPayload | OutbountRtpPayload;
export default formatGetStatsPayload;
